import { Container, Row, Col, Tab } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";
import internImg1 from "../assets/img/project-img1.png"; // Replace with actual images
import internImg2 from "../assets/img/project-img2.png"; // Add this import
import internImg3 from "../assets/img/project-img3.png"; // Add this import
import colorSharp2 from "../assets/img/color-sharp2.png";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Internships = () => {
  const internships = [
    {
      title: "Web Developer Intern",
      description: "Developed dynamic web applications and optimized backend processes.",
      imgUrl: internImg1,
    },
    {
      title: "Data Analyst Intern",
      description: "Analyzed large datasets to identify trends and provide actionable insights.",
      imgUrl: internImg2,
    },
    {
      title: "Machine Learning Intern",
      description: "Built predictive models and implemented machine learning algorithms for real-world applications.",
      imgUrl: internImg3,
    },
  ];

  return (
    <section className="internship" id="internships">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className={isVisible ? "animate__animated animate__fadeIn internship-content" : "internship-content"}>
                  <h2 className="section-title">Internships</h2>
                  <p className="section-description">
                    Throughout my career, I have undertaken several internships that helped me gain hands-on experience in software development, data analysis, and machine learning. These opportunities allowed me to work with cross-functional teams, solve real-world problems, and learn from industry experts.
                  </p>
                  <Tab.Container id="internships-tabs" defaultActiveKey="first">
                    <Tab.Content id="slideInUp" className={isVisible ? "animate__animated animate__slideInUp" : ""}>
                      <Tab.Pane eventKey="first">
                        <Row>
                          {internships.map((internship, index) => (
                            <Col key={index} sm={12} md={6} lg={4}>
                              <ProjectCard {...internship} />
                            </Col>
                          ))}
                        </Row>
                      </Tab.Pane>
                    </Tab.Content>
                  </Tab.Container>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2} alt="Background Decorative" />
    </section>
  );
};
